DROP TABLE IF EXISTS account; CREATE TABLE account ( id int(11) NOT NULL AUTO_INCREMENT, owner int(11) DEFAULT NULL, owner_type int(11) DEFAULT NULL, deleted tinyint(1) DEFAULT '0', created_by varchar(200) DEFAULT NULL, created_time datetime DEFAULT NULL, update_by varchar(200) DEFAULT NULL, update_time datetime DEFAULT NULL, user_name varchar(200) DEFAULT NULL, password varchar(200) DEFAULT NULL, mobile varchar(11) DEFAULT NULL, real_name varchar(40) DEFAULT NULL, email varchar(40) DEFAULT NULL, avatar_url varchar(100) DEFAULT NULL, invitation_code varchar(40) DEFAULT NULL, ip varchar(40) DEFAULT NULL, last_login_time datetime DEFAULT NULL, company_id int(11) DEFAULT NULL, open_id varchar(100) DEFAULT NULL, nick_name varchar(100) DEFAULT NULL, head_img_url varchar(100) DEFAULT NULL, union_id varchar(100) DEFAULT NULL, PRIMARY KEY (id), KEY index_owner (owner) ) ENGINE=InnoDB DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS company; CREATE TABLE company ( id int(11) NOT NULL AUTO_INCREMENT, owner int(11) DEFAULT NULL, owner_type int(11) DEFAULT NULL, deleted tinyint(1) DEFAULT '0', created_by varchar(200) DEFAULT NULL, created_time datetime DEFAULT NULL, update_by varchar(200) DEFAULT NULL, update_time datetime DEFAULT NULL, name varchar(200) DEFAULT NULL, location varchar(300) DEFAULT NULL, product_name varchar(200) DEFAULT NULL, logo_url varchar(100) DEFAULT NULL, industry int(11) DEFAULT NULL, PRIMARY KEY (id) ) ENGINE=InnoDB DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS dictionary; CREATE TABLE dictionary ( id int(11) NOT NULL AUTO_INCREMENT, name varchar(100) DEFAULT NULL, value varchar(100) DEFAULT NULL, tag varchar(100) DEFAULT NULL, PRIMARY KEY (id) ) ENGINE=InnoDB DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS employee; CREATE TABLE employee ( id int(11) NOT NULL AUTO_INCREMENT, owner int(11) DEFAULT NULL, owner_type int(11) DEFAULT NULL, deleted tinyint(1) DEFAULT '0', created_by varchar(200) DEFAULT NULL, created_time datetime DEFAULT NULL, update_by varchar(200) DEFAULT NULL, update_time datetime DEFAULT NULL, user_name varchar(200) DEFAULT NULL, password varchar(200) DEFAULT NULL, real_name varchar(40) DEFAULT NULL, PRIMARY KEY (id) ) ENGINE=InnoDB DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS wallet; CREATE TABLE wallet ( id int(11) NOT NULL AUTO_INCREMENT, owner int(11) DEFAULT NULL, owner_type varchar(20) DEFAULT NULL, deleted tinyint(1) DEFAULT '0', created_by varchar(40) DEFAULT NULL, created_time datetime DEFAULT NULL, update_by varchar(40) DEFAULT NULL, update_time datetime DEFAULT NULL, salt bigint(50) DEFAULT NULL, sign varchar(200) DEFAULT NULL, user_id int(11) DEFAULT NULL, user_type varchar(20) DEFAULT NULL, type varchar(40) DEFAULT NULL, currency varchar(40) DEFAULT NULL, status varchar(40) DEFAULT NULL, balance decimal(12,2) DEFAULT NULL, leger_type varchar(40) DEFAULT NULL, PRIMARY KEY (id) ) ENGINE=InnoDB DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS wallet_lot; CREATE TABLE wallet_lot ( id int(11) NOT NULL AUTO_INCREMENT, owner int(11) DEFAULT NULL, owner_type varchar(20) DEFAULT NULL, deleted tinyint(1) DEFAULT '0', created_by varchar(40) DEFAULT NULL, created_time datetime DEFAULT NULL, update_by varchar(40) DEFAULT NULL, update_time datetime DEFAULT NULL, salt bigint(50) DEFAULT NULL, sign varchar(200) DEFAULT NULL, wallet_id int(11) DEFAULT NULL, type varchar(40) DEFAULT NULL, balance decimal(12,2) DEFAULT NULL, priority int(11) DEFAULT NULL, PRIMARY KEY (id) ) ENGINE=InnoDB DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS wallet_lot_transaction; CREATE TABLE wallet_lot_transaction ( id int(11) NOT NULL AUTO_INCREMENT, owner int(11) DEFAULT NULL, owner_type varchar(20) DEFAULT NULL, deleted tinyint(1) DEFAULT '0', created_by varchar(40) DEFAULT NULL, created_time datetime DEFAULT NULL, update_by varchar(40) DEFAULT NULL, update_time datetime DEFAULT NULL, wallet_id int(11) DEFAULT NULL, wallet_lot_id int(11) DEFAULT NULL, wallet_transaction_id int(11) DEFAULT NULL, transaction_type varchar(40) DEFAULT NULL, lot_type varchar(40) DEFAULT NULL, tracking_uuid varchar(100) DEFAULT NULL, amount decimal(12,2) DEFAULT NULL, PRIMARY KEY (id) ) ENGINE=InnoDB DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS wallet_transaction; CREATE TABLE wallet_transaction ( id int(11) NOT NULL AUTO_INCREMENT, owner int(11) DEFAULT NULL, owner_type varchar(20) DEFAULT NULL, deleted tinyint(1) DEFAULT '0', created_by varchar(40) DEFAULT NULL, created_time datetime DEFAULT NULL, update_by varchar(40) DEFAULT NULL, update_time datetime DEFAULT NULL, wallet_id int(11) DEFAULT NULL, type varchar(40) DEFAULT NULL, tracking_uuid varchar(100) DEFAULT NULL, event varchar(40) DEFAULT NULL, reason varchar(40) DEFAULT NULL, amount decimal(12,2) DEFAULT NULL, original_balance decimal(12,2) DEFAULT NULL, current_balance decimal(12,2) DEFAULT NULL, PRIMARY KEY (id) ) ENGINE=InnoDB AUTO_INCREMENT=97 DEFAULT CHARSET=utf8;
